mapper.update:

UPDATE stdt
SET stdt_pwd=#{stdtPwd}, stdt_nm=#{stdtNm}, gender_cd=#{genderCd}, birth_dd=#{birthDd}, stdt_tel=#{stdtTel}, job_cd=#{jobCd}
WHERE stdt_id=#{stdtId}

mapper.select:

SELECT stdt_pwd
FROM stdt
WHERE stdt_id=#{stdtId}


SELECT *
FROM flup 
WHERE file_id = #{fileId} and file_sub_id = #{fileSubId}
