mapper.select:

select * 
from sbjt
where sbjt_id = (
select sbjt_id from lctr where lctr_id=#{lectureId}
)


select * 
from prof
where prof_id = (
select prof_id from lctr where lctr_id=#{lectureId}
)


select * 
from lctr
where lctr_id = #{lectureId}

SELECT clss_id AS "clssId", clss_nm AS "clssNm", limit_cnt AS "limitCnt", aply_start_dt as "aplyStartDt", aply_end_dt as "aplyEndDt", clss_start_dd AS "clssStartDd", clss_end_dd AS "clssEndDd", clss_adr AS "clssAdr", cmcd.cmcd_nm AS "clssCd" FROM clss JOIN cmcd on cmcd.tpcd_id||cmcd.cmcd_id = clss.clss_cd WHERE clss_nm LIKE CONCAT(#{className}, '%') AND cmcd.cmcd_nm IN
#{item}
AND (aply_start_dt >= #{aplyStartDt} AND aply_end_dt <= #{aplyEndDt}) AND (clss_start_dd >= #{classStartDd} AND clss_end_dd <= #{classEndDd}) ORDER BY clss_id
