mapper.select:

SELECT
	clss_nm
FROM clss WHERE clss_id = #{clssId}


SELECT
	count(stdt_id) wlogCnt
FROM wlog
WHERE
	clss_id=#{clssId}
	AND wlog_cd=#{wlogCd}
	AND stdt_id=#{stdtId}
	AND in_tm > to_date(#{startDate}, 'YYYY-MM-DD')
	AND out_tm <= to_date(#{endDate}, 'YYYY-MM-DD') + 1

SELECT clss_Id, mngr_Id, cmpy_id, clss_Nm, clss_Content, limit_Cnt, aply_Start_Dt, aply_End_Dt, clss_Start_Dd, clss_End_Dd, set_In_Tm, set_Out_Tm, m1.cmcd_nm as clss_Cd , clss_Adr, clss_Total_Tm, c.file_Id, clss_Etc FROM clss c JOIN cmcd m1 ON c.clss_cd = m1.cmcd_id WHERE mngr_id = #{mngrId} AND c.clss_cd IN
#{item}
ORDER BY clss_nm DESC
