mapper.select:

	SELECT
		clss_nm
	FROM clss WHERE clss_id = #{clssId}


	SELECT
		count(stdt_id) wlogCnt
	FROM wlog
	WHERE
		clss_id=#{clssId}
		AND wlog_cd=#{wlogCd}
		AND stdt_id=#{stdtId}
		AND delete_yn = 'N'


	
		AND in_tm > to_date(#{startDate}, 'YYYY-MM-DD')
	


	
		AND out_tm <= to_date(#{endDate}, 'YYYY-MM-DD') + 1

mapper.update:

	UPDATE rgst
	SET
		CMPT_cd=#{targetCmptId}
		,updt_dt=systimestamp
		,updter_id=#{mngrId}
	WHERE CLSS_ID=#{clssId}
		AND STDT_ID=#{stdtId}


	UPDATE mngr
	SET
		mngr_tel = #{mngrTel}
		,mngr_Nm = #{mngrNm}
	WHERE
		user_email=#{userEmail}


	UPDATE lgin
	SET
		user_pwd=#{userPwd}
	WHERE
		user_email=#{userEmail}


	UPDATE resn
	SET
		resn_cd=#{resnCd}
		,updt_dt=systimestamp
		,updter_id=#{mngrId}
	WHERE resn_id=#{resnId}


	UPDATE wlog
	SET
		wlog_cd=#{wlogCd}
		,updt_dt=systimestamp
		,updter_id=#{mngrId}
	WHERE wlog_id=#{wlogId}
