mapper.update:

UPDATE stdt
SET stdt_pwd=#{stdtPwd}, stdt_nm=#{stdtNm}, gender_cd=#{genderCd}, birth_dd=#{birthDd}, stdt_tel=#{stdtTel}, job_cd=#{jobCd}
WHERE stdt_id=#{stdtId}

mapper.select:

select COALESCE(COUNT(aply_id), 0) as "applyYN"
	from aply
	where stdt_id = #{stdtId}
		and clss_id = #{classId}

mapper.update:

update aply
set 
	aply_cd = #{aplyCd}
where aply_id=#{aplyId}


update wlog
set 
wlog_total_tm = #{totalTm},
	wlog_cd = #{outlogCd},
	out_tm = #{newOutTm}
where wlog_id = #{lastWlogId}
